+        for (int x = 0; x < iw; x++) {
+            for (int y = 0; y < ih; y++) {
+                Color color = pr.getColor(x,y);
+                if (color.getOpacity() != 1.0) {
+                    return false;
+                }
+            }
+        }
+        return true;
+    }
+
+    /**
+     * Snapshots the specified JavaFX {@link Image} object and stores a
+     * copy of its pixels into a {@link BufferedImage} object, creating
+     * a new object if needed.
