+
+    @Override
+    public final int compareTo(Marker m) {
+        return getTextPos().compareTo(m.getTextPos());
+    }
+
+    @Override
+    public int hashCode() {
+        int h = Marker.class.hashCode();
+        h = h * 31 + getTextPos().hashCode();
+        return h;
+    }
