+            this.attrs = attrs;
+        }
+
+        public String text() {
+            return text;
+        }
+
+        public StyleAttributeMap attrs() {
+            return attrs;
+        }
+
+        public void setAttrs(StyleAttributeMap a) {
+            attrs = a;
+        }
+
+        public StyleAttributeMap getStyleAttributeMap() {
+            return attrs;
+        }
+
