+            if (start == 0) {
+                if (end < len) {
+                    text = text.substring(end);
+                } else {
+                    text = "";
+                }
+            } else {
+                if (end < len) {
+                    text = text.substring(0, start) + text.substring(end, len);
+                } else {
+                    text = text.substring(0, start);
+                }
+            }
