+                        String s1 = toSplit.substring(0, ix);
+                        set(i++, new RSegment(s1, a));
+                        if (insertSegment2(i, text, attrs)) {
+                            i++;
+                        }
+                        if (ix < toSplit.length()) {
+                            String s2 = toSplit.substring(ix);
+                            insertSegment2(i, s2, a);
