+            int off = 0;
+            int i;
+            int ct = size();
+            for (i = 0; i < ct; i++) {
+                RSegment seg = get(i);
+                int len = seg.getTextLength();
+                if (offset < (off + len)) {
+                    if (offset != off) {
+                        // split segment
+                        StyleAttributeMap a = seg.attrs();
+                        String toSplit = seg.text();
+                        int ix = offset - off;
+                        String s1 = toSplit.substring(0, ix);
+                        String s2 = toSplit.substring(ix);
+                        set(i, new RSegment(s1, a));
+
+                        next.add(new RSegment(s2, a));
+                        i++;
+                    }
+                    break;
+                }
+                off += len;
+            }
