+                    StyleAttributeMap a = next.get(0).getStyleAttributeMap();
+                    add(new RSegment("", a));
+                }
+            }
+            if (next.size() == 0) {
+                if (size() > 0) {
+                    StyleAttributeMap a = get(size() - 1).getStyleAttributeMap();
