+            int ix0 = -1;
+            int off0 = 0;
+            int off = 0;
+            int ct = size();
+
+            // find start segment
+            int i = 0;
+            for (; i < ct; i++) {
+                RSegment seg = get(i);
+                int len = seg.getTextLength();
