+            if (ix0 == ix1) {
+                // same segment
+                RSegment seg = get(ix0);
+                if (seg.removeRegion(off0, off1)) {
+                    remove(ix0);
+                    if (size() == 0) {
+                        // keep attributes in a zero width segment
+                        add(new RSegment("", seg.getStyleAttributeMap()));
+                    }
+                }
+            } else {
+                // spans multiple segments
+                // first segment
+                RSegment seg = get(ix0);
+                if (seg.removeRegion(off0, Integer.MAX_VALUE)) {
+                    remove(ix0);
+                    ix1--;
+                    ct--;
+                } else {
+                    ix0++;
+                }
+                // last segment
+                if (ix1 < 0) {
+                    ix1 = ct;
+                } else {
