-                        int alpha = input[inOff++] & 0xff;
-                        float f = alpha / 255.0F;
-                        gray = (byte) (f * (gray & 0xff));
-                        output[outOff++] = gray;
-                        output[outOff++] = gray;
-                        output[outOff++] = gray;
-                        output[outOff++] = (byte) alpha;
