+                width, height, smooth ? Image.SCALE_SMOOTH : Image.SCALE_DEFAULT);
+        }
+
+        var metadata = new ImageMetadata(
+            null, true, null, null, null,
+            null, null, image.getWidth(), image.getHeight(), null,
+            null, null);
