+            assertTrue(Stream.of(node1, node2, node4, node5).allMatch(n -> n.getHoistingNode() == null));
+            assertSame(node4, node3.getHoistingNode());
+
+            node5.requestFocus();
+            assertIsFocused(node3);
+            assertNotFocused(node1, node2, node4, node5);
