+
+            // node1 . . . . . . . . . . []
+            //  node2 . . . . . . . . [focusScope]
+            //      node3 . . . . . . [hoistFocus]
+            //          node4 . . . . [hoistFocus]
+            scene.setRoot(
+                node1 = new N(false, false,
+                    node2 = new N(true, false,
+                        node3 = new N(false, true,
+                            node4 = new N(false, true, null)
