+        if (expressions.size() >= 2) {
+            Token nextToken = tokens.peek();
+            if (nextToken != null && otherKeyword.test(nextToken)) {
+                errorHandler.accept(tokens.consume(), "Unexpected token");
+                return false;
+            }
+        }
