+            for (Predicate<Token> predicate : predicates) {
+                if (consumeIf(predicate) == null) {
+                    return false;
+                }
+            }
+
+            return true;
+        } finally {
+            currentIndex = index;
+            currentItem = index >= 0 ? source.get(index) : null;
+        }
+    }
