+    public final void doSort(Comparator<? super E> comparator) {
+        var sortedList = new ArrayList<>(list);
+        sortedList.sort(comparator);
+        onProposedChange(Collections.unmodifiableList(sortedList), 0, size());
+        try {
+            modCount++;
+            list.setAll(sortedList);
+        } catch(Exception e) {
+            modCount--;
+            throw e;
+        }
+    }
