@@ -74,0 +74,13 @@
+    private static int getFirstInt(String input) {
+        String str = input;
+        if (str == null) {
+            return -1;
+        }
+        str = str.trim();
+        if (str.isEmpty() || !Character.isDigit(str.charAt(0))) {
+            return -1;
+        }
+        str = str.split("\\D+")[0];
+        return Integer.parseInt(str);
+    }
