+    VirtualFlow<?> getVirtualFlow() {
+        Parent p = getSkinnable();
+        while (p != null) {
+            if (p instanceof VirtualFlow vf) {
+                return vf;
+            }
+            p = p.getParent();
+        }
+        return null;
