+    /**
+     * Selects the specified value.
+     */
+    public void selectValue(T value) {
+        List<NamedValue<T>> items = getItems();
+        int sz = items.size();
+        for (int i = 0; i < sz; i++) {
+            NamedValue<T> item = items.get(i);
+            if (Objects.equals(value, item.getValue())) {
+                select(i);
+                return;
+            }
+        }
+    }
