+            Bounds b = binding.get();
+            double rx = b.getWidth() / 2.0;
+            double ry = b.getHeight() / 2.0;
+            setCenterX(rx);
+            setCenterY(ry);
+            setRadiusX(rx);
+            setRadiusY(ry);
+        }
+    }
