@@ -43,1 +43,1 @@
-- (void)_setResizable;
+- (void)_setResizable:(bool)resizable;

@@ -2,1 +2,1 @@
- * Copyright (c) 2011, 2024, Oracle and/or its affiliates. All rights reserved.
+ * Copyright (c) 2011, 2025, Oracle and/or its affiliates. All rights reserved.
@@ -215,1 +215,1 @@
-- (void)_setResizable
+- (void)_setResizable:(bool)resizable
@@ -217,13 +217,4 @@
-    NSUInteger mask = [self->nsWindow styleMask];
-    if ((mask & NSWindowStyleMaskResizable) != 0)
-    {
-        if (self->isDecorated == YES)
-        {
-            mask &= ~(NSUInteger)NSWindowStyleMaskResizable;
-            [self->nsWindow setStyleMask: mask];
-            [self->nsWindow setShowsResizeIndicator:NO];
-
-            NSButton *zoomButton = [self->nsWindow standardWindowButton:NSWindowZoomButton];
-            [zoomButton setEnabled:NO];
-        }
-        self->isResizable = NO;
+    self->isResizable = resizable;
+
+    if (self->isDecorated == NO) {
+        return;
@@ -231,12 +222,17 @@
-    else
-    {
-        if (self->isDecorated == YES)
-        {
-            mask |= NSWindowStyleMaskResizable;
-            [self->nsWindow setStyleMask: mask];
-            [self->nsWindow setShowsResizeIndicator:YES];
-
-            NSButton *zoomButton = [self->nsWindow standardWindowButton:NSWindowZoomButton];
-            [zoomButton setEnabled:YES];
-        }
-        self->isResizable = YES;
+
+    NSUInteger mask = [self->nsWindow styleMask];
+
+    if (resizable) {
+        mask |= NSWindowStyleMaskResizable;
+        [self->nsWindow setStyleMask: mask];
+        [self->nsWindow setShowsResizeIndicator:YES];
+
+        NSButton *zoomButton = [self->nsWindow standardWindowButton:NSWindowZoomButton];
+        [zoomButton setEnabled:YES];
+    } else {
+        mask &= ~(NSUInteger)NSWindowStyleMaskResizable;
+        [self->nsWindow setStyleMask: mask];
+        [self->nsWindow setShowsResizeIndicator:NO];
+
+        NSButton *zoomButton = [self->nsWindow standardWindowButton:NSWindowZoomButton];
+        [zoomButton setEnabled:NO];

@@ -474,2 +474,1 @@
-        window->isResizable = NO;
-        window->isDecorated = isTitled || isExtended;
+
@@ -505,0 +504,1 @@
+        window->isDecorated = isTitled || isExtended;
@@ -508,1 +508,2 @@
-
+        window->isResizable = NO;
+        [window _setResizable:NO]; // actual value will be set later with a separate JNI downcall
@@ -1164,4 +1165,1 @@
-        if (window->isResizable != jResizable)
-        {
-            [window performSelectorOnMainThread:@selector(_setResizable) withObject:nil waitUntilDone:YES];
-        }
+        [window _setResizable:jResizable];
