-task javadoc(type: Javadoc, dependsOn: createMSPfile) {
+task createOverviewFile() {
+    def srcDir = rootProject.projectDir
+    def dstDir = rootProject.buildDir
+    def fileName = "overview.html"
+
+    inputs.file(new File(srcDir, fileName))
+    outputs.file(new File(dstDir, fileName))
+
+    doLast {
+        copy {
+            from "$srcDir/$fileName"
+            into dstDir
+
+            filter { line ->
+                line.replaceAll("@JAVADOC_VERSION@", jfxReleaseMajorVersion)
+            }
+        }
+    }
+}
+
+task javadoc(type: Javadoc, dependsOn: [createMSPfile, createOverviewFile]) {
