@@ -353,13 +380,6 @@
-/**
- * htmlCurrentChar:
- * @ctxt:  the HTML parser context
- * @len:  pointer to the length of the char read
- *
- * The current char value, if using UTF-8 this may actually span multiple
- * bytes in the input buffer. Implement the end of line normalization:
- * 2.11 End-of-Line Handling
- * If the encoding is unspecified, in the case we find an ISO-Latin-1
- * char, then the encoding converter is plugged in automatically.
- *
- * Returns the current char value and its length
- */
+static int
+htmlMaskMatch(htmlAsciiMask mask, unsigned c) {
+    if (c >= 64)
+        return(0);
+    return((mask[c/32] >> (c & 31)) & 1);
+}
@@ -368,4 +388,30 @@
-htmlCurrentChar(xmlParserCtxtPtr ctxt, int *len) {
-    const unsigned char *cur;
-    unsigned char c;
-    unsigned int val;
+htmlValidateUtf8(xmlParserCtxtPtr ctxt, const xmlChar *str, size_t len,
+                 int partial) {
+    unsigned c = str[0];
+    int size;
+
+    if (c < 0xC2) {
+        goto invalid;
+    } else if (c < 0xE0) {
+        if (len < 2)
+            goto incomplete;
+        if ((str[1] & 0xC0) != 0x80)
+            goto invalid;
+        size = 2;
+    } else if (c < 0xF0) {
+        unsigned v;
+
+        if (len < 3)
+            goto incomplete;
+
+        v = str[1] << 8 | str[2]; /* hint to generate 16-bit load */
+        v |= c << 16;
+
+        if (((v & 0x00C0C0) != 0x008080) ||
+            ((v & 0x0F2000) == 0x000000) ||
+            ((v & 0x0F2000) == 0x0D2000))
+            goto invalid;
+
+        size = 3;
+    } else {
+        unsigned v;
@@ -373,2 +419,2 @@
-    if (ctxt->input->end - ctxt->input->cur < INPUT_CHUNK)
-        xmlParserGrow(ctxt);
+        if (len < 4)
+            goto incomplete;
@@ -376,2 +422,1 @@
-    if ((ctxt->input->flags & XML_INPUT_HAS_ENCODING) == 0) {
-        xmlChar * guess;
+        v = c << 24 | str[1] << 16 | str[2] << 8 | str[3];
@@ -379,20 +424,3 @@
-        /*
-         * Assume it's a fixed length encoding (1) with
