-                                "Char 0x%X out of allowed range\n", 0);
-                *len = 1;
-                return(' ');
-            } else {
-                *len = 0;
-                return(0);
-            }
-        }
-        /* 1-byte code */
-        *len = 1;
-        return(*ctxt->input->cur);
-    }
