+    while (!PARSER_STOPPED(ctxt)) {
+        if (avail == 0) {
+            ctxt->input->cur = cur;
+            GROW;
+            cur = ctxt->input->cur;
+            avail = ctxt->input->end - cur;
+
+            if (avail == 0)
