+        }
+
+        if (*cur == '\n') {
+            line++;
+            col = 1;
+        } else if (IS_WS_HTML(*cur)) {
+            col++;
+        } else {
+            break;
+        }
+
+        cur += 1;
+        avail -= 1;
+
+        if (res < INT_MAX)
+            res++;
