+    if (len < buf->size - buf->use)
+        return(0);
+    if (len >= INT_MAX - buf->use)
+        return(-1);
+
+    if (buf->size > (size_t) len) {
+        if (buf->size <= INT_MAX / 2)
+            size = buf->size * 2;
+        else
+            size = INT_MAX;
+    } else {
+        size = buf->use + len + 1;
+        if (size <= INT_MAX - 100)
+            size += 100;
+    }
