+                                             flags, &buf);
+    }
+    if (code != XML_ERR_OK)
+        return(code);
+
+    input = xmlNewInputInternal(buf, filename);
+    if (input == NULL)
+        return(XML_ERR_NO_MEMORY);
+
+#ifdef LIBXML_HTTP_ENABLED
+    code = xmlCheckHTTPInputInternal(input);
+    if (code != XML_ERR_OK) {
+        xmlFreeInputStream(input);
+        return(code);
+    }
+#endif
+
+    *out = input;
+    return(XML_ERR_OK);
+}
