+  guint8 u0_1, u2_1, u4_1;
+  guint8 v0_1, v2_1, v4_1;
+  guint8 y0_2, y1_2, y2_2, y3_2, y4_2, y5_2;
+  guint8 u0_2, u2_2, u4_2;
+  guint8 v0_2, v2_2, v4_2;
