+      a1 = y1_1 << 2 | (u2_1 << 12) | (y2_1 << 22);
+      a2 = v2_1 << 2 | (y3_1 << 12) | (u4_1 << 22);
+      a3 = y4_1 << 2 | (v4_1 << 12) | (y5_1 << 22);
