+            if ((res > ULONG_MAX) ||
+                (in->consumed > ULONG_MAX - (unsigned long)res))
+                in->consumed = ULONG_MAX;
+            else
+                in->consumed += res;
+        }
+    }
+
+    xmlBufUpdateInput(buf->buffer, in, used);
+}
