+		l.setLayoutData(new GridData(SWT.BEGINNING, SWT.CENTER, false, false, 2, 0));
+
+		Text t = createText(parent, hint);
+		t.setLayoutData(new GridData(SWT.FILL, SWT.CENTER, true, true, cols - 2, 0));
