+		l.setLayoutData(new GridData(SWT.BEGINNING, SWT.CENTER, false, false, 2, 0));
+
+		Combo c = createCombo(parent, items);
+		c.setLayoutData(new GridData(SWT.FILL, SWT.CENTER, true, true, cols - 2, 0));
