+		}
+
+		public Object get(int index) {
+			return values[index];
+		}
+
+		public void set(int index, Object value) {
+			values[index] = value;
+			// mark presence of the element so iteration can skip nulls quickly
+			mask |= (1L << index);
+		}
+	}
