+		if (outEdge != null) {
+			outEdge.value += value;
+		}
+		fromNode.getOut().put(new NodeWrapper(toNode.getNodeId(), toNode), edge);
+		toNode.getIn().put(new NodeWrapper(fromNode.getNodeId(), fromNode), edge);
+	}
