-			if (childTag == TAG_EVENTTYPE_V2) {
-				for (XMLTagInstance settingElement : child.getTagsInstances(TAG_SETTING)) {
-					changed |= settingElement.setValue(ATTRIBUTE_CONTROL_REFERENCE, null);
-				}
