-	private static String deriveHost(JMXServiceURL url) {
-		StringTokenizer st = new StringTokenizer(url.getURLPath(), ":/"); //$NON-NLS-1$
-		if (st.countTokens() == 5) {
-			for (int i = 0; i < 2; i++) {
-				st.nextToken();
-			}
-			String host = st.nextToken();
-			// strip dashes
-			return host;
-		}
-		return "unknown"; //$NON-NLS-1$
-	}
