-	private static int derivePort(JMXServiceURL url) {
-		StringTokenizer st = new StringTokenizer(url.getURLPath(), ":/"); //$NON-NLS-1$
-		if (st.countTokens() == 5) {
-			for (int i = 0; i < 3; i++) {
