+import org.openjdk.jmc.rjmx.common.RJMXCorePlugin;
+import org.openjdk.jmc.rjmx.common.preferences.JMXRMIPreferences;
+
+public final class JMXRMISystemPropertiesProvider {
+
+	public static void setup() {
+		try {
+			Properties jmxRmiProperties = JMXRMIPreferences.getInstance().getProperties();
+			if (jmxRmiProperties != null) {
+				for (String prop : jmxRmiProperties.stringPropertyNames()) {
+					Object val = jmxRmiProperties.get(prop);
+					if (val != null && !val.toString().isEmpty()) {
+						System.setProperty(prop, val.toString());
+					}
+				}
+			}
+		} catch (Exception e) {
+			RJMXCorePlugin.getDefault().getLogger().log(Level.FINE, "Did not load jmxRmiProperties", e); //$NON-NLS-1$
+		}
+	}
+
+	public static void clearJMXRMISystemProperties() {
+		try {
+			Properties jmxRmiProperties = JMXRMIPreferences.getInstance().getProperties();
+			if (jmxRmiProperties != null) {
+				for (String prop : jmxRmiProperties.stringPropertyNames()) {
+					System.clearProperty(prop);
+				}
+			}
+		} catch (Exception e) {
+			RJMXCorePlugin.getDefault().getLogger().log(Level.FINE, "Failed to remove JMXRMI SystemProperties", e); //$NON-NLS-1$
+		}
+	}
+
+	public static boolean isKeyStoreConfigured() {
+		try {
+			Properties jmxRmiProperties = JMXRMIPreferences.getInstance().getProperties();
+			if (jmxRmiProperties != null) {
+				int totalPrefCnt = 0;
+				for (String prop : jmxRmiProperties.stringPropertyNames()) {
+					Object val = jmxRmiProperties.get(prop);
+					if (val != null && !val.toString().isEmpty()) {
+						++totalPrefCnt;
+					}
+				}
+				if (totalPrefCnt == 4) {
+					return true;
+				}
+			}
+		} catch (Exception e) {
+			RJMXCorePlugin.getDefault().getLogger().log(Level.FINE, "Did not load jmxRmiProperties", e); //$NON-NLS-1$
+		}
+		return false;
+	}
+}

