+                    t = "report";
+                } else {
+                    throw new JemmyException("NIY - nor index.html nor report.html selected");
+                }
+            }
+            if (i == -1) {
+                throw new JemmyException("Url to html report page was not found. Tring to browse from '" + browser.getPath().toString() + "' to " + t);
+            }
+
+            return urls[i];
+        }
