+            if (!(new File(path + "html" + File.separator + "notRun.html").exists())) {
+                throw new JemmyException("passed.html was not created");
+            }
+        } else {
+            if (new File(path + "html" + File.separator + "notRun.html").exists()) {
+                throw new JemmyException("passed.html was created while unexpected");
