+        }
+                boolean someIsAlive = true;
+        while (ref.isOpNull() && someIsAlive) {
+                        someIsAlive = false;
+                        for (ComponentFinder finder : threads) {
+                                if (finder.isAlive) {
+                                        someIsAlive = true;
+                                }
+                                if (!ref.isOpNull())
+                                    break;
+                        }
+        }
+        if (!ref.isOpNull()) {
+            return (JComponent) ref.getOp().getLabelFor();
