+    boolean testRemoveFirst(Object[] data) {
+    DoublyLinkedList list = createList(data);
+    Object[] ref = copy(data);
+
+    for (int i = 0; i < ref.length; i++) {
+        int refLen = ref.length - i;
+        // select item to remove
+        Object o = ref[0];
+        // remove from list
