+    for (int i = 0; i < ref.length; i++) {
+        int refLen = ref.length - i;
+        // select item to remove
+        Object o = ref[refLen / 2];
+        // remove from list
+        list.remove(o);
+        // remove from reference array
+        if (remove(ref, 0, refLen, o))
+        refLen--;
