+    boolean remove(Object[] data, int offset, int length, Object o) {
+    for (int i = offset; i < offset + length; i++) {
+        boolean found = (data[i] == null ? o == null : data[i].equals(o));
+        if (found) {
+        System.arraycopy(data, i + 1, data, i, offset + length - i - 1);
+        return true;
+        }
+    }
+    // not found
