+            // data difference found
+            return false;
+        }
+        else if (!d.equals(ref[i]))
+            // data difference found
+            return false;
+        }
+        else
+        // list is longer than ref
+        return false;
+
+        i++;
+    }
+
+    return (i == offset + length);
