files.patch:
@@ -3,1 +3,1 @@
-    id 'org.jetbrains.intellij' version '1.14.1'
+    id 'org.jetbrains.intellij.platform' version '2.0.1'
@@ -21,0 +21,3 @@
+    intellijPlatform {
+        defaultRepositories()
+    }
@@ -25,0 +28,8 @@
+
+    intellijPlatform {
+        intellijIdeaCommunity('2024.2.0.2')
+        plugin("AntSupport:${antSupportVersion}")
+        bundledPlugins('com.intellij.java', 'TestNG-J')
+        instrumentationTools()
+    }
+
@@ -27,4 +38,9 @@
-// See https://github.com/JetBrains/gradle-intellij-plugin/
-intellij {
-    version = minBuild
-    plugins = ['java', 'AntSupport', 'TestNG-J']
+// See https://github.com/JetBrains/intellij-platform-gradle-plugin/
+intellijPlatform {
+    pluginConfiguration {
+        version = pluginVersion
+        ideaVersion {
+            sinceBuild = minBuild
+            untilBuild = ''
+        }
+    }
