+    static long[] al = {102, 108, 111, 119, 101, 114, 32, 102, 111, 114, 32,
+                        119, 104, 105, 99, 104, 32, 108, 111, 118, 101, 32,
+                        105, 115, 32, 116, 104, 101, 32, 104, 111, 110, 101,
+                        121, 46, 32, 87, 101, 32, 119, 105, 108, 108, 32, 115,
