+  ldrw(tmp, tmp);
+  lslv(dst, src, tmp);
+  lea(tmp, ExternalAddress(AOTRuntimeConstants::coops_base_address()));
+  ldr(tmp, tmp);
+  add(dst, dst, tmp);
+  pop(regs, sp);
+}
