-            if (! CACHE_LIFETIMES)  return;
-            Object[] objects = Thread.scopedCache();
-            if (objects == null) {
-                objects = createCache();
-            }
-            int slot = TABLE_SIZE + (chooseVictim(Thread.currentCarrierThread()) & 1);
-            objects[slot] = lt;
-        }
